mapper.select:
SELECT FROM AB01
order by ${orderField} ${orderSeq}
order by AAE036 desc

sql.where:
 AND AAB001  =#{aab001exists}  
 AND AAB001  =#{aab001exists}  
 AND AAB004  =#{aab004exists}  
 AND AAB007  =#{aab007exists}  
 AND AAB003  =#{aab003exists}  
AND CCPR08=0

mapper.select:
SELECT t.aae006,t.aab001,t.aab003,t.aab004,t.aae004,t.aae005,t.aab007,t.aab010,t.aab011,t.aab013,t.aab014, t.aaa021,t.aae036,t.aae019,t.aae020 ,y.name as aab020 ,f.name as aab054, s.name as aab021, b.name as aab023,n.name as aab022,g.name as cczy06 FROM AB01 t LEFT JOIN TAB_ECONOMIC y ON y.code=t.aab020 LEFT JOIN TAB_INDUSTRYCLASSES f ON f.code=t.aab054 LEFT JOIN TAB_RELATIONSHIP s ON s.code=t.aab021 LEFT JOIN TAB_DEPARTMENT b ON b.code=t.aab023 LEFT JOIN TAB_INDUSTRY n ON n.code=t.aab022 LEFT JOIN TAB_DATASOURCES g ON g.code=t.cczy06
order by ${orderField} ${orderSeq}
order by AAE036 desc

select * from ( select t.*, row_number() over(partition by t.aac001 order by t.aac001) rn2 from( SELECT t.aac001, t.aac003, t.bca112, t.ccmu01, t.acb223n, t.aac011n, t.ccmu15, t.aac038n, t.aac004 FROM ( SELECT * FROM (SELECT t2.aac001, t2.aac003, t2.aac004, t.bca112, t2.ccmu01, t3.ccmu15, t_wy.name as aac038n, t_ed.name as aac011n, f_get_age(to_char(sysdate, 'yyyy-mm-dd'), to_char(t2.aac006, 'yyyy-mm-dd')) as acb223n, row_number() over(partition by t2.aac001 order by t2.aac001) rn FROM cc20 t, ac01 t2, ccm01 t3, tab_workyears t_wy, tab_education t_ed WHERE t.aac001 = t2.aac001 and t3.ccmu01 = t2.ccmu01 and t2.aac038 = t_wy.code and t2.aac011 = t_ed.code and t.acb208 = 0 and t.bca111 in (select t.bca111 from cb21 t, ab01 t2 where t.aab001 = t2.aab001 and t2.ccmu01 =#{ccmu01})) WHERE rn = 1) t union all
SELECT t.aac001, t.aac003, t.bca112, t.ccmu01, t.acb223n, t.aac011n, t.ccmu15, t.aac038n, t.aac004 FROM v_wz_resume_list t
) t ) where rn2=1

select * from ab01 where 1=1
  AND aab004 = #{aab004}
   AND aab007 = #{aab007}

SELECT t.AAB004 as "aab004",t.AAB007 as "aab007",rbua.UA_NO as "uaNo" ,to_char(rbua.UA_CREATETIME,'yyyy-mm-dd') as "uaCreatetime" ,t.AAE004 as "aae004",t.AAE005 as "aae005" ,rbua.UA_NAME as "uaName",rbua.UA_PHONE as "uaPhone" ,rbua.UA_CARD as "uaCard",u.name as "aab019Name" ,rbua.UA_CONTENT as "uaContent" ,rbua.UA_STATUS as "uaStatus"
FROM AB01 t left join RSDA_BS_UNIT_AGREEMENT rbua on rbua.UA_UNIT_ID=t.AAB001 left join TAB_UNITNATURE u ON u.code=t.aab019
WHERE t.AAB001=#{aab001}
